import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

export default function Item() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [produto, setProduto] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`http://localhost:5000/produtos/${id}`)
      .then(res => {
        if (!res.ok) throw new Error('Produto não encontrado');
        return res.json();
      })
      .then(data => {
        setProduto(data);
        setLoading(false);
      })
      .catch(err => {
        alert(err.message);
        navigate('/paginaInicial');
      });
  }, [id, navigate]);

  const adicionarAoCarrinho = () => {
    fetch('http://localhost:5000/carrinho', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ produto_id: produto.id })
    })
      .then(res => res.json())
      .then(data => alert(data.mensagem || data.erro))
      .catch(() => alert('Erro ao adicionar ao carrinho'));
  };

  if (loading) return <div>Carregando...</div>;

  return (
    <div className="pagina-inicial" style={{ maxWidth: 600, margin: '40px auto' }}>
      <button className="botao-perfil" onClick={() => navigate('/paginaInicial')}>
        Voltar
      </button>

      <h1>{produto.nome}</h1>

      <img
        src={produto.imagem_url}
        alt={produto.nome}
        style={{ width: '100%', height: 'auto', borderRadius: 8, marginBottom: 20 }}
      />

      <p><strong>Preço:</strong> R$ {produto.preco.toFixed(2)}</p>

      <button className="botao-perfil" onClick={adicionarAoCarrinho}>
        Adicionar ao Carrinho
      </button>
    </div>
  );
}
