import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../App.css';

export default function AdicionarProduto() {
  const [nome, setNome] = useState('');
  const [preco, setPreco] = useState('');
  const [imagemUrl, setImagemUrl] = useState('');
  const [estoque, setEstoque] = useState(1);
  const [categoriaId, setCategoriaId] = useState('');
  const [categorias, setCategorias] = useState([]);
  const [novaCategoria, setNovaCategoria] = useState('');
  const navigate = useNavigate();

  const usuario = JSON.parse(localStorage.getItem('usuario'));

  useEffect(() => {
    fetch('http://localhost:5000/categorias')
      .then(res => res.json())
      .then(data => setCategorias(data))
      .catch(err => console.error('Erro ao buscar categorias:', err));
  }, []);

  const criarCategoria = () => {
    if (!novaCategoria.trim()) return;
    if (categorias.find(cat => cat.nome.toLowerCase() === novaCategoria.toLowerCase())) {
      alert('Categoria jÃ¡ existe');
      return;
    }

    fetch('http://localhost:5000/categorias', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ nome: novaCategoria })
    })
      .then(res => {
        if (!res.ok) throw new Error('Erro ao criar categoria');
        return res.json();
      })
      .then(data => {
        setCategorias([...categorias, { id: data.id, nome: novaCategoria }]);
        setCategoriaId(data.id);
        setNovaCategoria('');
      })
      .catch(err => alert(err.message));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!nome || !preco || !imagemUrl || !categoriaId) {
      alert('Preencha todos os campos');
      return;
    }

    fetch('http://localhost:5000/produtos', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'X-User-Id': usuario?.id?.toString() || ''
      },
      body: JSON.stringify({
        nome,
        preco: parseFloat(preco),
        imagem_url: imagemUrl,
        estoque: parseInt(estoque),
        categoria_id: parseInt(categoriaId)
      })
    })
      .then(res => {
        if (!res.ok) throw new Error('Erro ao adicionar produto');
        return res.json();
      })
      .then(() => {
        alert('Produto adicionado!');
        navigate('/paginaInicial');
      })
      .catch(err => alert(err.message));
  };

  return (
    <div className="container" style={{ maxWidth: 600, margin: '40px auto' }}>
      <h2>Adicionar Produto</h2>
      <form onSubmit={handleSubmit} className="formulario">
        <input
          type="text"
          placeholder="Nome do Produto"
          value={nome}
          onChange={e => setNome(e.target.value)}
          required
        />
        <input
          type="number"
          step="0.01"
          placeholder="PreÃ§o"
          value={preco}
          onChange={e => setPreco(e.target.value)}
          required
        />
        <input
          type="url"
          placeholder="URL da Imagem"
          value={imagemUrl}
          onChange={e => setImagemUrl(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Estoque"
          value={estoque}
          min={1}
          onChange={e => setEstoque(parseInt(e.target.value) || 1)}
          required
        />

        <div style={{ marginTop: 20 }}>
          <label>Categoria:</label>
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px', marginTop: 10 }}>
            {categorias.map(cat => (
              <button
                type="button"
                key={cat.id}
                onClick={() => setCategoriaId(cat.id)}
                style={{
                  padding: '8px 12px',
                  borderRadius: 6,
                  border: categoriaId === cat.id ? '2px solid green' : '1px solid #ccc',
                  backgroundColor: categoriaId === cat.id ? '#e0ffe0' : '#f9f9f9',
                  cursor: 'pointer'
                }}
              >
                {cat.nome}
              </button>
            ))}
          </div>
        </div>

        <div style={{ marginTop: 20 }}>
          <input
            type="text"
            placeholder="Nova Categoria"
            value={novaCategoria}
            onChange={e => setNovaCategoria(e.target.value)}
          />
          <button type="button" onClick={criarCategoria} style={{ marginLeft: 10 }}>
            Adicionar Categoria
          </button>
        </div>

        <button type="submit" style={{ marginTop: 20 }}>Salvar Produto</button>
      </form>

      <button
        onClick={() => navigate('/paginaInicial')}
        style={{
          marginTop: 20,
          backgroundColor: '#6c757d',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: 6,
          cursor: 'pointer'
        }}
      >
        Voltar
      </button>
    </div>
  );
}
