import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../App.css';

export default function AdicionarProduto() {
  const [nome, setNome] = useState('');
  const [preco, setPreco] = useState('');
  const [imagemUrl, setImagemUrl] = useState('');
  const [estoque, setEstoque] = useState(1);
  const [categoriaId, setCategoriaId] = useState('');
  const [categorias, setCategorias] = useState([]);
  const navigate = useNavigate();

  // Pega o usuário salvo no localStorage (após login)
  const usuario = JSON.parse(localStorage.getItem('usuario'));

  // Busca as categorias no backend ao montar o componente
  useEffect(() => {
    fetch('http://localhost:5000/categorias')
      .then(res => res.json())
      .then(data => setCategorias(data))
      .catch(err => console.error('Erro ao buscar categorias:', err));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!nome || !preco || !imagemUrl || !categoriaId) {
      alert('Por favor, preencha todos os campos, incluindo a categoria');
      return;
    }

    fetch('http://localhost:5000/produtos', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'X-User-Id': usuario?.id?.toString() || ''  // envia o ID do usuário no header
      },
      body: JSON.stringify({
        nome: nome,
        preco: parseFloat(preco),
        imagem_url: imagemUrl,
        estoque: parseInt(estoque, 10),
        categoria_id: parseInt(categoriaId, 10), // envia o ID da categoria
      }),
    })
      .then(res => {
        if (!res.ok) throw new Error('Erro ao adicionar produto');
        return res.json();
      })
      .then(() => {
        alert('Produto adicionado com sucesso!');
        navigate('/paginaInicial');
      })
      .catch(err => alert(err.message));
  };

  return (
    <div className="container" style={{ maxWidth: 500, margin: '40px auto' }}>
      <h2>Adicionar Produto</h2>
      <form onSubmit={handleSubmit} className="formulario">
        <input
          type="text"
          placeholder="Nome do Produto"
          value={nome}
          onChange={e => setNome(e.target.value)}
          required
        />
        <input
          type="number"
          step="0.01"
          placeholder="Preço"
          value={preco}
          onChange={e => setPreco(e.target.value)}
          required
        />
        <input
          type="url"
          placeholder="URL da Imagem"
          value={imagemUrl}
          onChange={e => setImagemUrl(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Estoque"
          value={estoque}
          min={1}
          onChange={e => setEstoque(parseInt(e.target.value, 10) || 1)}
          required
        />

        <select
          value={categoriaId}
          onChange={e => setCategoriaId(e.target.value)}
          required
        >
          <option value="">Selecione a categoria</option>
          {categorias.map(cat => (
            <option key={cat.id} value={cat.id}>
              {cat.nome}
            </option>
          ))}
        </select>

        <button type="submit">Salvar Produto</button>
      </form>
      <button
        onClick={() => navigate('/paginaInicial')}
        style={{
          marginTop: 20,
          backgroundColor: '#6c757d',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: 6,
          cursor: 'pointer'
        }}
      >
        Voltar
      </button>
    </div>
  );
}
