import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../App.css';

export default function PaginaInicial() {
  const [produtos, setProdutos] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:5000/produtos')
      .then(res => res.json())
      .then(data => setProdutos(data))
      .catch(err => console.error('Erro ao buscar produtos:', err));
  }, []);

  function irParaPerfil() {
    navigate('/perfil');
  }

  function abrirDetalhes(id) {
    navigate(`/produto/${id}`);
  }

  function irParaAdicionarProduto() {
    navigate('/adicionar-produto');
  }

  function irParaCarrinho() {
    navigate('/carrinho');
  }

  return (
    <div className="pagina-inicial">
      <header className="header">
        <h1>Feed de Produtos</h1>
        <div className="botoes-topo">
          <button className="botao-perfil" onClick={irParaPerfil}>
            Ver Perfil
          </button>
          <button
            className="botao-adicionar"
            onClick={irParaAdicionarProduto}
            style={{ marginLeft: '10px', backgroundColor: '#28a745' }}
          >
            Adicionar Produto
          </button>
          <button
            className="botao-carrinho topo"
            onClick={irParaCarrinho}
            aria-label="Ver carrinho"
            title="Ver carrinho"
            style={{ marginLeft: '10px' }}
          >
            <svg
              className="icone-carrinho"
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
              width="24"
              height="24"
            >
              <circle cx="9" cy="21" r="1"></circle>
              <circle cx="20" cy="21" r="1"></circle>
              <path d="M1 1h4l2.68 13.39a2 2 0 0 0 2 1.61h9.72a2 2 0 0 0 2-1.61L23 6H6"></path>
            </svg>
          </button>
        </div>
      </header>

      <div className="lista-produtos">
        {produtos.map(produto => (
          <div
            key={produto.id}
            className="card-produto"
            onClick={() => abrirDetalhes(produto.id)}
          >
            <img
              src={produto.imagem_url}
              alt={produto.nome}
              className="imagem-produto"
            />
            <h3>{produto.nome}</h3>
            <p>Pre√ßo: R$ {produto.preco.toFixed(2)}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
