import { useEffect, useState } from 'react'; // 游댢 adiciona useState aqui!
import { useNavigate } from 'react-router-dom';

export default function Perfil() {
  const navigate = useNavigate();

  const [usuario, setUsuario] = useState(null);
  const [produtosPostados, setProdutosPostados] = useState([]);

  useEffect(() => {
    try {
      const userFromStorage = JSON.parse(localStorage.getItem('usuario'));
      if (!userFromStorage) {
        navigate('/login');
      } else {
        setUsuario(userFromStorage);

        // busca os produtos postados pelo usu치rio
        fetch(`http://localhost:5000/produtos/usuario/${userFromStorage.id}`)
          .then(res => res.json())
          .then(data => setProdutosPostados(data))
          .catch(err => console.error("Erro ao buscar produtos do usu치rio", err));
      }
    } catch {
      navigate('/login');
    }
  }, [navigate]);

  if (!usuario) return null; // ou um "carregando..."

  return (
    <div>
      <div className="perfil-container">
        <h1>Perfil do Usu치rio</h1>
        <div className="perfil-box">
          <p><strong>Nome:</strong> {usuario.nome}</p>
          <p><strong>Email:</strong> {usuario.email}</p>
          <p><strong>CPF:</strong> {usuario.cpf}</p>
          <p><strong>CEP:</strong> {usuario.cep}</p>
          <p><strong>Data de Nascimento:</strong> {usuario.data_nascimento}</p>
          <p><strong>Idade:</strong> {usuario.idade}</p>
        </div>
        <button onClick={() => navigate('/paginaInicial')} style={{ marginTop: '20px' }}>
          Voltar para P치gina Inicial
        </button>
      </div>

      <div className="produtosPostados" style={{ background: '#eee', padding: '20px', marginTop: '30px' }}>
        <h2>Produtos Postados</h2>

        {produtosPostados.length === 0 ? (
          <p>Nenhum produto postado ainda.</p>
        ) : (
          <div className="lista-produtos">
            {produtosPostados.map(produto => (
              <div key={produto.id} className="card-produto">
                <img src={produto.imagem_url} alt={produto.nome} className="imagem-produto" />
                <h3>{produto.nome}</h3>
                <p>Pre칞o: R$ {produto.preco.toFixed(2)}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
