import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Carrinho() {
  const [produtos, setProdutos] = useState([]);
  const [valorTotal, setValorTotal] = useState('0.00'); // inicializa string formatada
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:5000/carrinho')
      .then(res => res.json())
      .then(data => {
        setProdutos(data.produtos);
        setValorTotal(data.valor_total || '0.00');
      })
      .catch(err => alert('Erro ao carregar carrinho'));
  }, []);

    function remover_do_carrinho(e, produtoId) {
    e.stopPropagation(); // evita abrir detalhes quando clicar no carrinho
    fetch('http://localhost:5000/carrinho', {
      method: 'DELETE',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ produto_id: produtoId }),
    })
      .then(res => res.json())
      .then(data => alert(data.mensagem || data.erro))
      .catch(() => alert('Erro ao adicionar ao carrinho'));
  }

  return (
    <div className="pagina-inicial">
      <button className="botao-perfil" onClick={() => navigate('/paginaInicial')}>
        Voltar
      </button>
      <h1>Carrinho de Compras</h1>

      <div className="lista-produtos">
        {produtos.map(produto => (
          <div key={produto.id} className="card-produto">
            <img src={produto.imagem_url} alt={produto.nome} className="imagem-produto" />
            <h3>{produto.nome}</h3>
            <p>Pre√ßo: R$ {produto.preco.toFixed(2)}</p>
                        <button
              className="botao-carrinho card"
              onClick={(e) => remover_do_carrinho(e, produto.id)}
              aria-label={`Adicionar ${produto.nome} ao carrinho`}
              title="Adicionar ao carrinho"
            >
              <svg
                className="icone-carrinho"
                xmlns="http://www.w3.org/2000/svg"
                viewBox="0 0 24 24"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
                width="20"
                height="20"
              >
                <circle cx="9" cy="21" r="1"></circle>
                <circle cx="20" cy="21" r="1"></circle>
                <path d="M1 1h4l2.68 13.39a2 2 0 0 0 2 1.61h9.72a2 2 0 0 0 2-1.61L23 6H6"></path>
              </svg>
            </button>
          </div>
        ))}
      </div>

      <div className="carrinho-footer">
        <span className="precoTotal">Total: R$ {valorTotal}</span>
        <button className="confirmarCompra">Confirmar compra</button>
      </div>
    </div>
  );
}
