import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../App.css';

export default function PaginaInicial() {
  const [produtos, setProdutos] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:5000/produtos')
      .then(res => res.json())
      .then(data => setProdutos(data))
      .catch(err => console.error('Erro ao buscar produtos:', err));
  }, []);

  function irParaPerfil() {
    navigate('/perfil');
  }

  function irParaAdicionarProduto() {
    navigate('/adicionar-produto');
  }

  function irParaCarrinho() {
    navigate('/carrinho');
  }

  function abrirDetalhes(id) {
    navigate(`/produto/${id}`);
  }

  function adicionarAoCarrinho(produtoId) {
    fetch('http://localhost:5000/carrinho', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ produto_id: produtoId })
    })
    .then(res => res.json())
    .then(data => alert(data.mensagem || data.erro))
    .catch(() => alert('Erro ao adicionar ao carrinho'));
  }

  return (
    <div className="pagina-inicial">
      <header className="header">
        <h1>Feed de Produtos</h1>
        <div>
          <button className="botao-perfil" onClick={irParaPerfil}>Ver Perfil</button>
          <button 
            className="botao-adicionar" 
            onClick={irParaAdicionarProduto} 
            style={{ marginLeft: '10px', backgroundColor: '#28a745' }}
          >
            Adicionar Produto
          </button>
          <button 
            className="botao-carrinho topo" 
            onClick={irParaCarrinho}
          >
            Carrinho
          </button>
        </div>
      </header>

      <div className="lista-produtos">
        {produtos.map(produto => (
          <div 
            key={produto.id} 
            className="card-produto"
          >
            <img 
              src={produto.imagem_url} 
              alt={produto.nome} 
              className="imagem-produto"
              onClick={() => abrirDetalhes(produto.id)}
            />
            <h3>{produto.nome}</h3>
            <p>Pre√ßo: R$ {produto.preco.toFixed(2)}</p>
            <button
              className="botao-carrinho"
              onClick={() => adicionarAoCarrinho(produto.id)}
              title="Adicionar ao carrinho"
            >
              <svg
                className="icone-carrinho"
                xmlns="http://www.w3.org/2000/svg"
                viewBox="0 0 24 24"
                strokeWidth="2"
                stroke="currentColor"
                fill="none"
                strokeLinecap="round"
                strokeLinejoin="round"
              >
                <circle cx="9" cy="21" r="1" />
                <circle cx="20" cy="21" r="1" />
                <path d="M1 1h4l2.68 13.39a2 2 0 0 0 2 1.61h9.72a2 2 0 0 0 2-1.61L23 6H6" />
              </svg>
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
