import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Perfil() {
  const navigate = useNavigate();

  useEffect(() => {
    const usuario = localStorage.getItem('usuario');
    if (!usuario) navigate('/login');
  }, [navigate]);

  let usuario = null;
  try {
    usuario = JSON.parse(localStorage.getItem('usuario'));
  } catch {
    usuario = null;
  }

  if (!usuario) return null;

  const [produtosPostados, setProdutosPostados] = useState([]);

useEffect(() => {
  const usuario = JSON.parse(localStorage.getItem('usuario'));
  if (!usuario) return;

  fetch(`http://localhost:5000/produtos/usuario/${usuario.id}`)
    .then(res => res.json())
    .then(data => setProdutosPostados(data))
    .catch(err => console.error("Erro ao buscar produtos do usu치rio", err));
}, []);


  return (
    <div>
      <div className="perfil-container">
        <h1>Perfil do Usu치rio</h1>
        <div className="perfil-box">
          <p><strong>Nome:</strong> {usuario.nome}</p>
          <p><strong>Email:</strong> {usuario.email}</p>
          <p><strong>CPF:</strong> {usuario.cpf}</p>
          <p><strong>CEP:</strong> {usuario.cep}</p>
          <p><strong>Data de Nascimento:</strong> {usuario.data_nascimento}</p>
          <p><strong>Idade:</strong> {usuario.idade}</p>
        </div>
        <button onClick={() => navigate('/paginaInicial')} style={{ marginTop: '20px' }}>
          Voltar para P치gina Inicial
        </button>
      </div>

      <div className="produtosPostados" style={{ background: '#eee', padding: '20px', marginTop: '30px' }}>
        <h1>pinto</h1>
      </div>
    </div>
  );
}
