import { useNavigate } from 'react-router-dom';

export default function PaginaInicial() {
  // ... seu código atual

  const navigate = useNavigate();

  function abrirDetalhes(id) {
    navigate(`/produto/${id}`); // essa rota deve ser configurada para abrir o componente Item
  }

  return (
    <div className="pagina-inicial">
      <button className="botao-perfil" onClick={() => navigate('/perfil')}>
        Ver Perfil
      </button>

      <h1>Feed de Produtos</h1>
      <div className="lista-produtos">
        {produtos.map((produto) => (
          <div
            key={produto.id}
            className="card-produto"
            onClick={() => abrirDetalhes(produto.id)}
            style={{ cursor: 'pointer' }}
          >
            <img src={produto.imagem_url} alt={produto.nome} className="imagem-produto" />
            <h3>{produto.nome}</h3>
            <p>Preço: R$ {produto.preco.toFixed(2)}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
