import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

export default function Item() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [produto, setProduto] = useState(null);
  const [loading, setLoading] = useState(true);
  const [noCarrinho, setNoCarrinho] = useState(false);

  useEffect(() => {
    fetch(`http://localhost:5000/produtos/${id}`)
      .then(res => {
        if (!res.ok) throw new Error('Produto não encontrado');
        return res.json();
      })
      .then(data => {
        setProduto(data);
        setLoading(false);
      })
      .catch(err => {
        alert(err.message);
        navigate('/paginaInicial');
      });
  }, [id, navigate]);

  useEffect(() => {
    fetch('http://localhost:5000/carrinho')
      .then(res => res.json())
      .then(data => {
        const estaNoCarrinho = data.some(item => item.id === Number(id));
        setNoCarrinho(estaNoCarrinho);
      })
      .catch(() => setNoCarrinho(false));
  }, [id]);

  if (loading) return <div>Carregando...</div>;

  function adicionarCarrinho() {
    fetch('http://localhost:5000/carrinho', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ produto_id: produto.id })
    })
      .then(res => res.json())
      .then(data => {
        alert(data.mensagem || data.erro);
        if (data.mensagem) setNoCarrinho(true);
      })
      .catch(() => alert('Erro ao adicionar ao carrinho'));
  }

  function removerCarrinho() {
    fetch(`http://localhost:5000/carrinho/${produto.id}`, {
      method: 'DELETE',
    })
      .then(res => res.json())
      .then(data => {
        alert(data.mensagem || data.erro);
        if (data.mensagem) setNoCarrinho(false);
      })
      .catch(() => alert('Erro ao remover do carrinho'));
  }

  return (
    <div className="pagina-inicial" style={{ maxWidth: 600, margin: '40px auto' }}>
      <button className="botao-perfil" onClick={() => navigate('/paginaInicial')}>
        Voltar
      </button>
      <h1>{produto.nome}</h1>
      <img
        src={produto.imagem_url}
        alt={produto.nome}
        style={{ width: '100%', height: 'auto', borderRadius: 8, marginBottom: 20 }}
      />
      <p><strong>Preço:</strong> R$ {produto.preco.toFixed(2)} <strong>Estoque: </strong></p>

      {!noCarrinho && (
        <button className="botao-perfil" onClick={adicionarCarrinho}>
          Adicionar ao Carrinho
        </button>
      )}

      {noCarrinho && (
        <button className="botao-perfil" onClick={removerCarrinho} style={{ backgroundColor: '#dc3545' }}>
          Remover do Carrinho
        </button>
      )}
    </div>
  );
}
