import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../App.css';

export default function PaginaInicial() {
  const [produtos, setProdutos] = useState([]);
  const [produtosNoCarrinho, setProdutosNoCarrinho] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:5000/produtos')
      .then(res => res.json())
      .then(data => setProdutos(data))
      .catch(err => console.error('Erro ao buscar produtos:', err));

    fetch('http://localhost:5000/carrinho')
      .then(res => res.json())
      .then(data => setProdutosNoCarrinho(data.map(prod => prod.id)))
      .catch(err => console.error('Erro ao buscar carrinho:', err));
  }, []);

  function irParaPerfil() {
    navigate('/perfil');
  }

  function abrirDetalhes(id) {
    navigate(`/produto/${id}`);
  }

  function irParaAdicionarProduto() {
    navigate('/adicionar-produto');
  }

  function irParaCarrinho() {
    navigate('/carrinho');
  }

  function adicionarAoCarrinho(id) {
    fetch('http://localhost:5000/carrinho', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ produto_id: id })
    })
      .then(res => res.json())
      .then(data => {
        alert(data.mensagem || data.erro);
        if (data.mensagem) {
          setProdutosNoCarrinho(prev => [...prev, id]);
        }
      })
      .catch(() => alert('Erro ao adicionar ao carrinho'));
  }

  function removerDoCarrinho(id) {
    fetch(`http://localhost:5000/carrinho/${id}`, {
      method: 'DELETE',
    })
      .then(res => res.json())
      .then(data => {
        alert(data.mensagem || data.erro);
        if (data.mensagem) {
          setProdutosNoCarrinho(prev => prev.filter(pid => pid !== id));
        }
      })
      .catch(() => alert('Erro ao remover do carrinho'));
  }

  return (
    <div className="pagina-inicial">
      <header className="header">
        <h1>Feed de Produtos</h1>
        <div>
          <button className="botao-perfil" onClick={irParaPerfil}>Ver Perfil</button>
          <button 
            className="botao-adicionar" 
            onClick={irParaAdicionarProduto} 
            style={{ marginLeft: '10px', backgroundColor: '#28a745' }}
          >
            Adicionar Produto
          </button>
          <button
            className="botao-carrinho"
            onClick={irParaCarrinho}
            style={{ marginLeft: '10px', backgroundColor: '#ffc107', color: '#000' }}
          >
            Carrinho ({produtosNoCarrinho.length})
          </button>
        </div>
      </header>

      <div className="lista-produtos">
        {produtos.map(produto => (
          <div key={produto.id} className="card-produto">
            <img 
              src={produto.imagem_url} 
              alt={produto.nome} 
              className="imagem-produto"
              onClick={() => abrirDetalhes(produto.id)}
              style={{ cursor: 'pointer' }}
            />
            <h3>{produto.nome}</h3>
            <p>Pre√ßo: R$ {produto.preco.toFixed(2)}</p>
            {produtosNoCarrinho.includes(produto.id) ? (
              <button
                className="botao-carrinho"
                onClick={() => removerDoCarrinho(produto.id)}
                style={{ backgroundColor: '#dc3545', marginTop: '10px' }}
              >
                Remover do Carrinho
              </button>
            ) : (
              <button
                className="botao-carrinho"
                onClick={() => adicionarAoCarrinho(produto.id)}
                style={{ marginTop: '10px' }}
              >
                Adicionar ao Carrinho
              </button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
