import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../App.css';

export default function PaginaInicial() {
  const [produtos, setProdutos] = useState([]);
  const [busca, setBusca] = useState('');
  const navigate = useNavigate();

  // Simula busca no backend (você pode substituir por um fetch real depois)
  useEffect(() => {
    // Exemplo de dados mockados
    const produtosMock = [
      { id: 1, nome: 'Camiseta Azul', preco: 49.99 },
      { id: 2, nome: 'Tênis Branco', preco: 199.90 },
      { id: 3, nome: 'Calça Jeans', preco: 99.50 },
      { id: 4, nome: 'Boné Preto', preco: 29.90 },
    ];
    setProdutos(produtosMock);
  }, []);

  // Filtrar produtos pela barra de busca
  const produtosFiltrados = produtos.filter(p =>
    p.nome.toLowerCase().includes(busca.toLowerCase())
  );

  return (
    <div className="pagina-inicial">
      <header className="topo">
        <h2>Loja Virtual</h2>

        {/* Botão de perfil */}
        <button onClick={() => navigate('/perfil')} className="botao-perfil">
          Meu Perfil
        </button>
      </header>

      {/* Barra de busca */}
      <input
        type="text"
        placeholder="Buscar produtos..."
        value={busca}
        onChange={(e) => setBusca(e.target.value)}
        className="barra-pesquisa"
      />

      {/* Lista de produtos */}
      <div className="lista-produtos">
        {produtosFiltrados.length > 0 ? (
          produtosFiltrados.map((produto) => (
            <div key={produto.id} className="produto">
              <h3>{produto.nome}</h3>
              <p>R$ {produto.preco.toFixed(2)}</p>
              <button>Ver mais</button>
            </div>
          ))
        ) : (
          <p>Nenhum produto encontrado.</p>
        )}
      </div>
    </div>
  );
}
